mapper.update:
UPDATE t_user
SET state = 0
WHERE id = #{userId}

UPDATE t_user
SET user_name = #{user.userName}, password = #{user.password}, state = #{user.state}
WHERE id = #{user.id}

mapper.select:
SELECT count(1)
FROM t_user
WHERE user_name = #{userName}
AND password = #{password}
AND state = 1
